CREATE TABLE `testimonials` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `testimonial` text NOT NULL,
  `testimonial_author` varchar(50) NOT NULL,
  `date_added` datetime DEFAULT NULL,
  `last_update` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=INNODB  DEFAULT CHARSET=latin1;

INSERT INTO `testimonials` VALUES(4, 'For all the love care and support you gave to dad and also to mum.  Eternally grateful, we salute you for your care dedication and professionalism. It is second to none.', 'Daughter of WW', '2012-06-01 13:05:42', '2012-06-01 13:05:42');
INSERT INTO `testimonials` VALUES(5, 'We could see from the level of management organisation, clear records of assessment and care and from speaking to people using and working for the agency that a highly personal and professional service was being offered.', 'CQC Inspector - Key Inspection 2009', '2012-06-01 14:04:23', '2012-06-01 14:04:23');
INSERT INTO `testimonials` VALUES(6, 'The care and understanding of mums needs was given in the most professional of ways.  Mum was shown dignity and this does not stop even after she died where the girls were fantastic...the whole team showed they really cared about doing their job, which does not happen often', 'daughter of JH Barrow', '2012-06-01 14:05:33', '2012-06-01 14:05:33');
INSERT INTO `testimonials` VALUES(7, 'Your wonderful team  not only provided care and more but showed tenderness, compassion  and in her final precious weeks became the family she lacked and provided both emotional and physical support both she and I needed', 'Friend of JD - Ulverston', '2012-06-01 14:05:55', '2012-06-01 14:05:55');

CREATE TABLE `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `article_content` text NOT NULL,
  `title` varchar(100) NOT NULL,
  `article_image_url` varchar(100) NULL,
  `thumbnail_image_url` varchar(100) NULL,
  `date_added` datetime DEFAULT NULL,
  `last_update` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=INNODB  DEFAULT CHARSET=latin1;

INSERT INTO `news` VALUES(4, '<p>\r\n	After a recent tendering exercise Lakeland Care were successful in becoming one of only four preferred providers appointed by the County Council. This means that Lakeland Care will expand their service provision for the whole of the Furness Area.&nbsp; The contract will run for 4 years which means that the Company will have secured growth for the medium term. They were also appointed as one of only six companies eligible to provide extra care housing across the whole of Cumbria.<br />\r\n	<br />\r\n	Managing Director Marge Smyth said &ldquo; this is a major success for us, I am very proud of the growth we have already seen in a relatively small timescale but to be awarded a contract of this size is tremendous. Our success is underpinned by our commitment to quality service provision delivered by a very dedicated and compassionate staff team who work enormously hard to make a difference to the lives of those who are most vulnerable in our community. This hard work has now&nbsp; been rewarded by a secure future for our staff team of sixty,&nbsp; a rare event in today&rsquo;s economic climate.&nbsp; In the tendering exercise we competed&nbsp; against some big national companies and were one of only a very small number of local independent providers awarded preferred provider status. We can now look forward to a very positive future&rdquo;</p>\r\n', 'Lakeland Care wins major new contract from Cumbria County Council', NULL, NULL, '2012-04-10 14:56:31', '2012-04-10 14:56:31');
INSERT INTO `news` VALUES(5, '<p>\r\n	Lakeland Care hosted a charity bingo night in the Coronation Hall in January which raised money for the local branch of the MND Association<br />\r\n	<br />\r\n	The money will be spent on equipment for local sufferers of this debilitating neurological condition<br />\r\n	<br />\r\n	See here accepting the cheque is Glenis Sharp and her husband Ian Sharp of Barrow the cheque was presented by Jo Ward, Care Manager at Lakeland Care</p>\r\n', 'Bingo Night raises Â£200 for local Motor Neurone Disease Association', '/images/admin/news/5/news-article-5-article.jpeg', '/images/admin/news/5/news-article-5-thumbnail.jpeg', '2012-04-10 15:48:36', '2012-06-07 09:45:43');
